import { useEffect } from "react";
import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import MatchingListPage from "./pages/MatchingListPage";
import FilteringPage from "./pages/FilteringPage";
import MyProfilePage from "./pages/MyProfilePage";
import LoginPage from "./pages/LoginPage";
import SignUpPage from "./pages/SignUpPage";
import MatchingProcessPage from "./pages/MatchingProcessPage";
import MatchingPage from "./pages/MatchingPage";
import MainPage from "./pages/MainPage";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
      case "/filteringpage":
        title = "";
        metaDescription = "";
        break;
      case "/myprofilepage":
        title = "";
        metaDescription = "";
        break;
      case "/loginpage":
        title = "";
        metaDescription = "";
        break;
      case "/signuppage":
        title = "";
        metaDescription = "";
        break;
      case "/matchingprocesspage":
        title = "";
        metaDescription = "";
        break;
      case "/matchingpage":
        title = "";
        metaDescription = "";
        break;
      case "/mainpage":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Routes>
      <Route path="/" element={<MainPage />} />
      <Route path="/filteringpage" element={<FilteringPage />} />
      <Route path="/myprofilepage" element={<MyProfilePage />} />
      <Route path="/loginpage" element={<LoginPage />} />
      <Route path="/signuppage" element={<SignUpPage />} />
      <Route path="/matchingprocesspage" element={<MatchingProcessPage />} />
      <Route path="/matchingpage" element={<MatchingPage />} />
      <Route path="/matchinglistpage" element={<MatchingListPage />} />
      <Route path="/SignUpSuccesspage" element={<SignUpSuccessPage />} />
    </Routes>
  );
}
export default App;
